import React from "react";
import "./App.css";

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = "";
  }

  handleText(ev) {
    const listenText = ev.target.value;
    console.log("funciono");
    console.log(listenText);
  }
  render() {
    return (
      <>
        <input type="text" className="App" onKeyUp={this.handleText} />
        <p copyText={this.handleText}></p>
      </>
    );
  }
}

export default App;
