import React from "react";
import "./App.css";

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = "";
    this.handleText = this.handleText.bind(this);
  }

  handleText(ev) {
    const listenText = ev.target.value;
    this.setState();
    console.log("funciono");
    console.log(listenText);
  }

  render() {
    return (
      <>
        <input type="text" className="App" onKeyUp={this.handleText} />
        <p value={this.listenText}></p>
      </>
    );
  }
}

export default App;
